jQuery(function () {
    jQuery("#main-wrapper").AdminSettings();
    
    jQuery(".left-sidebar").hover(
        function () {
            jQuery(".navbar-header").addClass("expand-logo");
        },
        function () {
            jQuery(".navbar-header").removeClass("expand-logo");
        }
    );
    // this is for close icon when navigation open in mobile view
    jQuery(".nav-toggler").on('click', function () {
        jQuery("#main-wrapper").toggleClass("hide-sidebar");
        jQuery(".nav-toggler i").toggleClass("ti-menu");
    });
    jQuery(".nav-lock").on('click', function () {
        jQuery("body").toggleClass("lock-nav");
        jQuery(".nav-lock i").toggleClass("mdi-toggle-switch-off");
        jQuery("body, .page-wrapper").trigger("resize");
    });
    // ============================================================== 
    // Resize all elements
    // ============================================================== 
    jQuery("body, .page-wrapper").trigger("resize");
    jQuery(".page-wrapper").delay(20).show();
    // AL SER CARGADO POR PRIMERA VEZ CONSIDERO ELEMENTO EL INICIO
    element = jQuery('ul#sidebarnav a[href="./');
    element.parentsUntil(".sidebar-nav").each(function (index){
        if(jQuery(this).is("li") && jQuery(this).children("a").length !== 0){
            jQuery(this).children("a").addClass("active");
            jQuery(this).parent("ul#sidebarnav").length === 0 ? jQuery(this).addClass("active"): jQuery(this).addClass("selected");
        }else if(!jQuery(this).is("ul") && jQuery(this).children("a").length === 0){
            jQuery(this).addClass("selected");
        }else if(jQuery(this).is("ul")){
            jQuery(this).addClass('in');
        }
    });
    element.addClass("active");
    // CADA VEZ QUE SE NAVEGUE EN EL MENU ACTIVO LAS CLASSES
    jQuery('#sidebarnav a').on('click', function(e){
        if (!jQuery(this).hasClass("active")){
            // hide any open menus and remove all other classes
            jQuery("ul", jQuery(this).parents("ul:first")).removeClass("in");
            jQuery("a", jQuery(this).parents("ul:first")).removeClass("active");

            // open our new menu and add the open class
            jQuery(this).next("ul").addClass("in");
            jQuery(this).addClass("active");
        }else if (jQuery(this).hasClass("active")){
            jQuery(this).removeClass("active");
            jQuery(this).parents("ul:first").removeClass("active");
            jQuery(this).next("ul").removeClass("in");
        }
    });
    jQuery('#sidebarnav >li >a.has-arrow').on('click', function (e) {
        e.preventDefault();
    });
    // Disable right click and f12
    /*
    jQuery("html").on("contextmenu",function(e){
       return false;
    });
    jQuery(document).keydown(function (event) {
        if (event.keyCode == 123) { // Prevent F12
            return false;
        } else if (event.ctrlKey && event.shiftKey && event.keyCode == 73) { // Prevent Ctrl+Shift+I        
            return false;
        }   
    });*/
});